insert  into `cache_filter`(`cid`,`data`,`expire`,`created`,`headers`,`serialized`) values ('2:b06b691ad50a3ce1a4ad47c78ad437f1','<h3>Welcome to the website of E &amp; E Potts Ltd.</h3>\n<p>Please take a moment to have a look at our products and services. We guarantee our parts will save you money and keep you on the move. We stock <em>full engines</em>, <em>long motors</em>, <em>short motors</em> and a wide range of parts. We have the expertise to supply quality goods at the price the customer expects to pay. We ship world-wide.</p>\n',1308087606,1308001206,'',0),('1:5024aa3b72cb95aff1b53168ed6f6ed1','<dl>\n<dt>Telephone: +44 (0) 161 737 4666\n<dt>Fax: +44 (0) 161 745 7063\n<dt>Email: <a href=\"mailto:enquiries@eepottsltd.com\">enquiries@eepottsltd.com</a>\n</dt></dt></dt></dl>\n',1308087607,1308001207,'',0),('2:4f96b3a226e0317b29c6906208d3ae58','<h4>E &amp; E Potts Ltd</h4>\n<p>Engine House<br />\nNew Thomas Street<br />\nSalford<br />\nManchester<br />\nM6 6WF<br />\nUK</p>\n',1308087607,1308001207,'',0),('2:83bf22f8485ac4fac73794a8814bc6c2','<h3>E &amp; E Potts Ltd started in 1956 supplying used engines and parts.</h3>\n<p>After a number of years the company started to ship truck engines and components to many parts of the world. In the late 1980s a 5000 square foot factory was acquired and we started to supply major companies with engines and parts for their rebuilding programs.</p>\n<p>The next 10 years saw a big growth in this business and a 20,000 square foot factory was purchased. Good running engines and quality core parts were shipped to customers in both large and small quantities and this business continues today.</p>\n<p>E &amp; E Potts Ltd started to receive enquiries from customers in the UK and overseas asking us to supply rebuilt engines, so we purchased them and sold them on. Enquiries kept coming in for long engines and short motors so we searched around but the engine rebuilder\'s did not supply long engines, only short motors. When we looked at the prices we decided that we could supply a quality long engine and a short motor with a guarantee for less money so we set up a rebuilding area and now produce short motors, long engines, cylinder blocks, cylinder heads, crankshafts, and all other related parts.</p>\n<p>In addition to this service we now import brand new quality Cummins parts for B.series and C.series engines at a price that our competitors find hard to match.</p>\n',1308087649,1308001249,'',0);

